import React from 'react'
import axios from 'axios'
import Timer from '../components/Time'

class Home extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            tasks : []
        }
    }

    fetchUncompletedTask = () =>{
        let local = localStorage.getItem("token")
        if(JSON.parse(local) != null){
            const token = {
                headers : {Authorization : "Bearer "+JSON.parse(local)}
            }
            axios.post('http://127.0.0.1:5000/getuncompletedtask',{},token).then
            (res =>{
                this.setState({
                    tasks : res.data.tasks
                })
            }).catch(error => console.log(error))
        }
    }

    componentDidMount = ()=>{
        this.fetchUncompletedTask()
    }

    render(){
        return (
            <div className = "container-fluid">
                <div className = "container-fluid">
                    <div className="mx-3">
                        <label className="ml-1 mt-2">Start Date and Time</label>
                        <input type = "datetime-local" className="form-control" onChange={this.handleChange}  name="startDate"></input>
                    </div>
                    <div className="mx-3">
                        <label className="ml-1 mt-2">End Date and Time</label>
                        <input type = "datetime-local" className="form-control" onChange={this.handleChange}  name="endDate"></input>
                    </div>
                    <button className="btn btn-primary mt-3 ml-3 mb-3" onClick={this.handleClick}>Get</button>
                </div>
                <div className = "row m-3 justify-content-center">
                    {this.state.tasks.map(ele => 
                        <Timer label = {ele}/>
                    )}
                </div>
            </div>
        )
    }

}

export default Home
